mapper.insert:
insert into work_tag_mapping (work_id, tag_id, tag_type,source_id,status,order_num,CREATE_TIME,MODIFY_TIME) values
(#{item.workId}, #{item.tagId}, #{item.tagType},#{item.sourceId},#{item.status},#{item.orderNum},#{item.createTime},#{item.modifyTime})
on duplicate key update status = VALUES(status), order_num =VALUES(order_num),MODIFY_TIME =VALUES(MODIFY_TIME) ;
